    private void Btn_TambahMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Btn_TambahMouseClicked
        // TODO add your handling code here:  
       java.util.Date tgl_pemasukkan = (java.util.Date) this.Txt_Tanggal.getDate();
       String kodepemasukkan = this.Txt_KodePemasukkan.getText();
        String noanggota = this.Txt_No_Anggota.getText();
        String namaanggota = this.Txt_Nama_Anggota.getText();
        String nominal = this.Txt_Nominal.getText();
        
        if (Txt_KodePemasukkan.getText().isEmpty()){
            JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Kode Pemasukkan Tidak Boleh Kosong");
        }else{
            if (Txt_No_Anggota.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Nomor Anggota Tidak Boleh Kosong");
            }else{
                if(Txt_Nama_Anggota.getText().isEmpty()){
                    JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Nama Anggota Tidak Boleh Kosong");
                }else{
                    if (Txt_Nominal.getText().isEmpty()){
                        JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Nominal Pemasukkan Tidak Boleh Kosong");
                    }else{
                        try{
                            Connection c = koneksiDatabases.getkoneksi();
                            String sql = "Insert into pemasukkan values(?,?,?,?,?)";
                            PreparedStatement p = (PreparedStatement) c.prepareStatement(sql);
                            p.setString(1, kodepemasukkan);
                            p.setString(2, noanggota);
                            p.setString(3, namaanggota);
                            p.setString(4, nominal);
                            p.setDate(5, new java.sql.Date(tgl_pemasukkan.getTime()));
                            p.executeUpdate();
                            p.close();
                            JOptionPane.showMessageDialog(this, "Sukses Tambah Data");
                        }catch (SQLException e){
                            System.out.println(e);
                        }finally{ 
                        }
                    }
                }
            }
        }try{
            Connection c = koneksiDatabases.getkoneksi();
            Statement s = c.createStatement();
            String sql = "Select kd_pemasukkan, nomor_anggota, nama_anggota, nominal_pemasukkan, tgl_pemasukkan from pemasukkan";
            ResultSet r = s.executeQuery(sql);
            TblPemasukkan.setModel(DbUtils.resultSetToTableModel(r));
        }catch (SQLException e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_Btn_TambahMouseClicked

    private void Btn_EditMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Btn_EditMouseClicked
        // TODO add your handling code here:  
         java.util.Date tgl_pemasukkan = (java.util.Date) this.Txt_Tanggal.getDate();
         String kodepemasukkan = this.Txt_KodePemasukkan.getText();
        String noanggota = this.Txt_No_Anggota.getText();
        String namaanggota = this.Txt_Nama_Anggota.getText();
        String nominal = this.Txt_Nominal.getText();
    
        
        if (Txt_KodePemasukkan.getText().isEmpty()){
            JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Kode Pemasukkan Tidak Boleh Kosong");
        }else{
            if (Txt_No_Anggota.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Nomor Anggota Tidak Boleh Kosong");
            }else{
                if(Txt_Nama_Anggota.getText().isEmpty()){
                    JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Nama Anggota Tidak Boleh Kosong");
                }else{
                    if (Txt_Nominal.getText().isEmpty()){
                        JOptionPane.showMessageDialog(this, "Data Belum Lengkap, Nominal Pemasukkan Tidak Boleh Kosong");
                    }else{
                        try{
                            Connection c=koneksiDatabases.getkoneksi();
                            String sql="UPDATE `siku1`.`pemasukkan` SET `kd_pemasukkan`=?,`nama_anggota`=?,`nominal_pemasukkan`=?,`tgl_pemasukkan`=? where nomor_anggota=?";
                            PreparedStatement p=(PreparedStatement) c.prepareStatement(sql);
                            p.setString(5, noanggota);
                            p.setString(1, kodepemasukkan);
                            p.setString(2, namaanggota);
                            p.setString(3, nominal);
                            p.setDate(4, new java.sql.Date(tgl_pemasukkan.getTime()));
                            p.executeUpdate();
                            p.close();
                            JOptionPane.showMessageDialog(this, "Sukses Update Data");
                        }catch (SQLException e){
                            System.out.println(e);
                        }finally{ 
                        }
                    }
                }
            }
        }try{
            Connection c = koneksiDatabases.getkoneksi();
            Statement s = c.createStatement();
            String sql = "Select kd_pemasukkan, nomor_anggota, nama_anggota, nominal_pemasukkan, tgl_pemasukkan from pemasukkan";
            ResultSet r = s.executeQuery(sql);
            TblPemasukkan.setModel(DbUtils.resultSetToTableModel(r));
        }catch (SQLException e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_Btn_EditMouseClicked

    private void Txt_No_AnggotaCaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_Txt_No_AnggotaCaretUpdate
        // TODO add your handling code here: 
         try{
            Connection c = koneksiDatabases.getkoneksi();
            Statement s = c.createStatement();
            String sql = "Select * from anggota where no_anggota='" + this.Txt_No_Anggota.getText() + "'";
            ResultSet r = s.executeQuery(sql);
            
            while (r.next()){
                this.Txt_Nama_Anggota.setText(r.getString("nama_anggota"));
            }
            r.close();
            s.close();
        }catch(SQLException e){
            System.out.println("Terjadi Kesalahan" + e);
        }
    }//GEN-LAST:event_Txt_No_AnggotaCaretUpdate

    private void Btn_CariMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Btn_CariMouseClicked
        // TODO add your handling code here: 
        DefaultTableModel tabelTampil1= new DefaultTableModel();
        tabelTampil1.addColumn("kd_pemasukkan");
          tabelTampil1.addColumn("nomor_anggota");
          tabelTampil1.addColumn("nama_anggota");
          tabelTampil1.addColumn("nominal_pemasukkan");
          tabelTampil1.addColumn("tgl_pemasukkan");
        try{
          Connection c=koneksiDatabases.getkoneksi();
          Statement s= c.createStatement();
          String sql="Select * from pemasukkan where nomor_anggota like '%"+ Txt_Cari.getText() + "%'" + "or nama_anggota like '%" + Txt_Cari.getText()+ "%'"+ "or nominal_pemasukkan like '%" + Txt_Cari.getText()+ "%'"+ "or tgl_pemasukkan like '%" + Txt_Cari.getText()+ "%'" + "or kd_pemasukkan like '%" + Txt_Cari.getText()+ "%'";
          ResultSet r=s.executeQuery(sql);
          while (r.next()){
           tabelTampil1.addRow(new Object[]{
            r.getString(1),r.getString(2),r.getString(3),r.getString(4),r.getString(5),
    });}
              TblPemasukkan.setModel(tabelTampil1);
              }catch(Exception e){}
    }//GEN-LAST:event_Btn_CariMouseClicked

    private void Btn_HapusMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Btn_HapusMouseClicked
        // TODO add your handling code here: 
         String noanggota=this.Txt_No_Anggota.getText();
        try {
                Connection c = koneksiDatabases.getkoneksi();
                String sql = "Delete from pemasukkan Where nomor_anggota=?";
                PreparedStatement p=(PreparedStatement) c.prepareStatement(sql);
                p.setString(1, noanggota);          

                p.executeUpdate();
                p.close();
                JOptionPane.showMessageDialog(this, "Sukses Hapus Data");
            }catch(SQLException e){
                System.out.println(e);
            }finally{
            }
         try {        
                Connection c=koneksiDatabases.getkoneksi();
                Statement s= c.createStatement();
                String sql="select *from pemasukkan";
                ResultSet r=s.executeQuery(sql);
                TblPemasukkan.setModel(DbUtils.resultSetToTableModel(r));
            }catch (Exception e){
            JOptionPane.showMessageDialog(null, e);
    }          
    }//GEN-LAST:event_Btn_HapusMouseClicked

    private void Txt_KodePemasukkanCaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_Txt_KodePemasukkanCaretUpdate
        // TODO add your handling code here: 
        try{ 
            Connection c = koneksiDatabases.getkoneksi(); 
            Statement s = c.createStatement(); 
           String sql = "Select * from pemasukkan where kd_pemasukkan='" + this.Txt_KodePemasukkan.getText() + " ' "; 
           ResultSet r = s.executeQuery(sql);
            while(r.next()){
                this.Txt_No_Anggota.setText(r.getString(2));
                this.Txt_Nama_Anggota.setText(r.getString("nama_anggota"));
                this.Txt_Nominal.setText(r.getString("nominal_pemasukkan")); 
                this.Txt_Tanggal.setDate(r.getDate("tgl_pemasukkan"));
            }
            r.close();
            s.close();
        }catch(SQLException e){
            System.out.println("Terjadi Kesalahan" + e);
        }

    }//GEN-LAST:event_Txt_KodePemasukkanCaretUpdate

